mapper.select:
SELECT r.id, r.code, r.pid, r.receipt_code, r.batch_no, r.detection_way, r.goods_id,
g.code AS goodsCode, g.title AS goodsTitle, g.unit_id,
r.type, r.censorship_qty, r.inspect_qty, r.qualified_qty, r.unqualified_qty, r.disposal, r.status, r.source, r.tester, r.test_result, r.custom_id, r.amount, r.return_type, r.original_price, r.price, r.create_time, r.create_user, r.update_time, r.update_user FROM quality_storage_reserva r
LEFT JOIN produce_product g ON g.id = r.goods_id
LEFT JOIN produce_material g ON g.id = r.goods_id
WHERE r.deleted = 0
AND g.product_mode = #{sr.productMode}
AND r.id = #{sr.id}
AND r.code = #{sr.code}
AND r.receipt_code = #{sr.receiptCode}
AND r.batch_no = #{sr.batchNo}
AND r.detection_way = #{sr.detectionWay}
AND r.goods_id = #{sr.goodsId}
AND g.code LIKE CONCAT('%', #{sr.goodsCode}, '%')
AND r.type = #{sr.type}
AND r.disposal = #{sr.disposal}
AND r.status = #{sr.status}
AND r.source = #{sr.source}
AND r.tester = #{sr.tester}
AND r.return_type = #{sr.returnType}
ORDER BY r.create_time DESC
