SELECT user_id AS id, p.code FROM sys_user_role ur LEFT JOIN sys_role r ON ur.role_id = r.id LEFT JOIN sys_role_permission rp ON r.id = rp.role_id LEFT JOIN sys_permission p ON rp.permission_id = p.id WHERE ur.user_id IN (
#{userId}
) AND r.status = 1 AND p.status = 1

SELECT DISTINCT p.code FROM sys_permission p INNER JOIN sys_role_permission rp ON p.id = rp.permission_id WHERE p.deleted = 0 AND p.status = 1 AND rp.role_id IN (
#{roleId}
)
